<?phpclass Daire_Sec extends PDO { function __construct() {        parent::__construct('mysql:host=localhost;dbname=site_management', 'root','');        header("Content-type:text/html; Charset=iso-8859-9");            if($_POST){                    /* blok seçimi */                   if($_POST["blok_id"]){                        $site_id = $_POST["site_id"];                        $blok_id = $_POST["blok_id"];                        $get = $this->prepare("SELECT * FROM tbl_House WHERE h_siteid=".$site_id." AND h_blokid=".$blok_id);                        $get->execute();                        $get->setFetchMode(PDO::FETCH_ASSOC);                        $daire = $get->fetchAll();                            echo '<option value="">..SEÇÝNÝZ..</option>';                            for($i = 0 ; $i < count($daire) ; $i++){                                echo '<option value='.$daire[$i]["id"].'>'.$daire[$i]["h_number"].'</option>';                            }                            //echo '<option>'.$il_id."-".$ilce_id.'</option>';                    }            }        }}new Daire_Sec();?>