<?phpclass Daire_Sec extends PDO { function __construct() {        parent::__construct('mysql:host=localhost;dbname=site_management', 'root','');        header("Content-type:text/html; Charset=iso-8859-9");            if($_POST){                    /* blok seçimi */                   if($_POST["blok_id"]){                        $site_id = $_POST["site_id"];                        $blok_id = $_POST["blok_id"];                        $house_id = $_POST["house_id"];                        $get = $this->prepare("SELECT ht.id,ht.h_namesurname FROM tbl_Host ht 												LEFT JOIN tbl_Blok b ON b.id = ht.h_blokid												LEFT JOIN tbl_House he ON he.id = ht.h_houseid												WHERE ht.h_blokid = $blok_id AND he.h_siteid = $site_id AND he.id = $house_id");                        $get->execute();                        $get->setFetchMode(PDO::FETCH_ASSOC);                        $ev_sahibi = $get->fetchAll();                            echo '<option value="">..SEÇÝNÝZ..</option>';                            for($i = 0 ; $i < count($ev_sahibi) ; $i++){                                echo '<option value='.$ev_sahibi[$i]["id"].'>'.$ev_sahibi[$i]["h_namesurname"].'</option>';                            }                            //echo '<option>'.$il_id."-".$ilce_id.'</option>';                    }            }        }}new Daire_Sec();?>